clcclear allwarning off allclose allpkg load signal% Carga de la señal modulada en FM[x, fs] = audioread(strcat(genpath(fullfile(pwd,'../audios/')),'senal_modulada_fm.wav'));% Me quedo con uno de los canales de audiox = x(:,1);% Aproximo la DEPcalc_DEP(x, fs,'fn');% Frecuencia de corte normalizada de la estación a filtrarwn = [0.12 0.24];%Diezmado a aplicard = 25;[audio, fs] = FM_DEMOD_PEREYRA(x, fs, wn, d);% Reproductor de audioplayer = audioplayer(audio, fs, 8);play (player);